                    { 90, null, "Nad-4", "<span style=\"font-size:13px\">The soil profile develops on deluvial sediments of the slope</span>", null, "Yamalo-Nenets Autonomous District, the surroundings of the city of Nadym", true, true, 1536310234L, "Stagnic Folic Cambisol", null, "This soil profile is located in the middle part of the long slope of the transition surface from the Ob-Nadym interfluve to the low terrace of the Nadym River", null, 162 },
