                    { 145, null, "Arh-5", null, null, "Russia, Arkhangelsk region, Primorsky district, White Sea-Kuloi plateau", true, true, 1537512927L, "Albic Podzol", "Spruce grass-shrub-green forest", "The soil profile is located on the slope of the valley of the small river (steepness of the slope is 5-10 degrees)", null, 221 },
                    { 146, null, "Arh-6", null, null, "Россия, Архангельская область, Приморский район, Беломорско-Кулойское плато", false, true, 1537514725L, "Корневой ход в подзоле при близком залегании к поверхности почвы каменисто-суглинистой морены", null, null, null, 223 },
